from fastapi import FastAPI, HTTPException, Depends, status
from fastapi.middleware.cors import CORSMiddleware
from fastapi.security import HTTPBearer, HTTPAuthorizationCredentials
from sqlalchemy.orm import Session
from sqlalchemy import func
from datetime import timedelta
from typing import List
import random

from pydantic import BaseModel

from .database import get_db, Base, engine
from .models import User, Candidate, Vote
from . import models, schemas, database
from datetime import datetime, timezone
from pytz import timezone as tz

import smtplib
from email.mime.text import MIMEText
from email.mime.multipart import MIMEMultipart

from dotenv import load_dotenv
import os

load_dotenv() 

MAIL_USERNAME = os.getenv("MAIL_USERNAME")
MAIL_PASSWORD = os.getenv("MAIL_PASSWORD")
print(MAIL_USERNAME, MAIL_PASSWORD)


from jose import jwt, JWTError
from .utils.jwt_handler import SECRET_KEY, ALGORITHM

from .utils.hashing import hash_password, verify_password
from .utils.jwt_handler import (
    create_access_token,
    decode_access_token,
    ACCESS_TOKEN_EXPIRE_MINUTES,
)

WIB = tz("Asia/Jakarta")

def to_utc(dt: datetime):
    """Konversi datetime naive/WIB ke UTC"""
    if dt.tzinfo is None:
        dt = WIB.localize(dt)  # kalau naive ‚Üí anggap WIB
    return dt.astimezone(timezone.utc)

security = HTTPBearer()

def get_current_user(credentials: HTTPAuthorizationCredentials = Depends(security)):
    token = credentials.credentials
    try:
        payload = jwt.decode(token, SECRET_KEY, algorithms=[ALGORITHM])
        username: str = payload.get("sub")
        if username is None:
            raise HTTPException(
                status_code=status.HTTP_401_UNAUTHORIZED,
                detail="Invalid authentication credentials",
            )
        return username
    except JWTError:
        raise HTTPException(
            status_code=status.HTTP_401_UNAUTHORIZED,
            detail="Invalid token",
        )

app = FastAPI()

class VotingPeriodBase(BaseModel):
    start_date: datetime
    end_date: datetime

class VotingPeriodCreate(VotingPeriodBase):
