frontend/src/styles/HomePage.css.content:
/* === GLOBAL === */
body {
  margin: 0;
  padding: 0;
  background-color: #0a0a0a;
  color: #f0f0f0;
  font-family: sans-serif;
  line-height: 1.6;
  padding-top: 80px;
  /* biar konten turun sejajar navbar */
  overflow: auto;
}

.homepage {
  margin-top: 28px;
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
  justify-content: flex-start;
}

.homepage-title {
  font-size: 2.5rem;
  margin-top: 20px;
  margin-bottom: 20px;
  color: #f0f;
  justify-content: center;
  align-items: center;
  text-shadow: 0 0 10px #f0f, 0 0 20px #f0f;
}

.homepage-subtitle {
  font-size: 1.5rem;
  /* ukuran agak besar */
  color: #0ff;
  /* neon cyan sesuai vibe futuristik */
  text-shadow:
    0 0 5px #0ff,
    0 0 10px #0ff,
    0 0 20px #0ff;
  /* efek glow */
  margin-bottom: 30px;
  /* jarak dengan konten bawah */
  font-weight: 500;
  /* sedikit tebal tapi tidak terlalu berat */
  letter-spacing: 1px;
  /* spasi huruf agar lebih futuristik */
  text-align: center;
  /* center alignment */
}

/* opsi animasi glow lembut */
@keyframes glowPulse {
  0% {
    text-shadow: 0 0 5px #0ff, 0 0 10px #0ff, 0 0 20px #0ff;
  }

  50% {
    text-shadow: 0 0 10px #0ff, 0 0 20px #0ff, 0 0 40px #0ff;
  }

  100% {
    text-shadow: 0 0 5px #0ff, 0 0 10px #0ff, 0 0 20px #0ff;
  }
}

.homepage-subtitle {
  animation: glowPulse 2s infinite alternate;
}


.container {
  text-align: center;
  padding: 20px;
}

.page-title {
  font-size: 2rem;
  margin-bottom: 20px;
  color: #0ff;
  text-shadow: 0 0 10px #0ff, 0 0 20px #0ff;
}

/* === Grid untuk kartu kandidat === */
.card-container {
  display: grid;
  grid-template-columns: repeat(3, minmax(280px, 1fr));
  gap: 25px;
  justify-content: center;
  align-items: stretch;
  margin-top: 30px;
  max-width: 1000px;
  margin-left: auto;
  margin-right: auto;
}

/* === Efek kartu kandidat === */
.card {
  width: 100%;
  height: 360px;
  perspective: 1000px;
  cursor: pointer;
}

.card-inner {
  position: relative;
  width: 100%;
  height: 100%;
  transition: transform 0.8s;
  transform-style: preserve-3d;
}

.card-inner.flipped {
  transform: rotateY(180deg);
}

.card-front,
.card-back {
  position: absolute;
  width: 100%;
  height: 100%;
  backface-visibility: hidden;
  border: 3px solid #0ff;
  border-radius: 15px;
  box-shadow: 0 0 20px #0ff, inset 0 0 30px rgba(0, 255, 255, 0.3);
  overflow: hidden;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
}

/* === Depan kartu === */
.card-front {
  background: linear-gradient(145deg, #111, #1a1a1a);
}

.card-front img {
  width: 100%;
  height: 70%;
  object-fit: cover;
  border-bottom: 2px solid #0ff;
}

.card-front h3 {
  margin: 10px 0 0;
  color: #0ff;
  font-size: 1.2em;
  text-shadow: 0 0 5px #0ff, 0 0 10px #0ff;
}

/* === Belakang kartu === */
.card-back {
  background: linear-gradient(145deg, #1a1a1a, #111);
  transform: rotateY(180deg);
  padding: 20px;
  text-align: left;
  color: #ddd;
}

.card-back h4 {
  color: #f0f;
  margin-bottom: 10px;
  text-shadow: 0 0 10px #f0f, 0 0 20px #f0f;
}

.card-back p {
  font-size: 0.95em;
  line-height: 1.5;
  color: #ccc;
}

/* === Wrapper kandidat (kartu + tombol) === */
.candidate-wrapper {
  display: flex;
  flex-direction: column;
  align-items: center;
}

/* === Tombol Vote === */
.vote-btn {
  margin-top: 24px;
  padding: 10px 20px;
  border: 2px solid #0ff;
  border-radius: 12px;
  background: transparent;
  color: #0ff;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  text-transform: uppercase;
  letter-spacing: 1px;
  transition: all 0.3s ease;
  box-shadow: 0 0 10px #0ff, 0 0 20px #0ff;
}

.vote-btn:hover {
  background: #0ff;
  color: #0a0a0a;
  box-shadow: 0 0 20px #0ff, 0 0 40px #0ff;
  transform: scale(1.05);
}

.info-voting, .statistik, .aturan {
  margin-top: 40px;
  padding: 20px;
  border-radius: 12px;
  background: linear-gradient(145deg, #111, #1a1a1a);
  border: 2px solid #0ff;
  box-shadow: 0 0 15px #0ff, inset 0 0 25px rgba(0, 255, 255, 0.2);
  color: #eee;
}

.info-voting h2, .statistik h2, .aturan h2 {
  color: #f0f;
  text-shadow: 0 0 8px #f0f, 0 0 15px #f0f;
  margin-bottom: 15px;
}

.info-voting p, .statistik p, .aturan li {
  color: #ccc;
  font-size: 0.95rem;
  line-height: 1.5;
}

.section-grid {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 25px;
  max-width: 1000px;
  margin: 40px auto;
  padding: 0 20px;
}

.aturan {
  grid-column: span 2;
}

/* Progress Bar */
.progress-bar {
  background: #222;
  border: 2px solid #0ff;
  border-radius: 8px;
  overflow: hidden;
  height: 20px;
  margin: 10px 0;
  box-shadow: inset 0 0 10px #0ff;
}

.progress {
  background: linear-gradient(90deg, #0ff, #09f);
  height: 100%;
  box-shadow: 0 0 10px #0ff;
}

/* === Countdown Section === */
.countdown {
  text-align: center;
  margin: 40px auto;
  padding: 20px;
  border: 2px solid #0ff;
  border-radius: 15px;
  background: linear-gradient(145deg, #111, #1a1a1a);
  box-shadow: 0 0 15px #0ff, inset 0 0 20px rgba(0, 255, 255, 0.3);
  max-width: 700px;
}

.countdown h2 {
  color: #f0f;
  text-shadow: 0 0 10px #f0f, 0 0 20px #f0f;
  margin-bottom: 20px;
}

.countdown h3 {
  font-size: 1.3rem;
  color: #0ff;
  /* neon cyan */
  text-shadow:
    0 0 5px #0ff,
    0 0 10px #0ff,
    0 0 20px #0ff;
  text-align: center;
  margin-top: 15px;
  font-weight: 500;
  letter-spacing: 1px;
  animation: glowPulse 2s infinite alternate;
  /* sama animasi glow seperti subtitle */
}


.countdown-timer {
  display: flex;
  justify-content: center;
  gap: 30px;
}

.countdown-timer div {
  text-align: center;
  min-width: 70px;
  background: #000;
  padding: 15px;
  border-radius: 10px;
  border: 2px solid #0ff;
  box-shadow: 0 0 10px #0ff;
}

.countdown-timer span {
  font-size: 2rem;
  font-weight: bold;
  color: #0ff;
  text-shadow: 0 0 10px #0ff, 0 0 15px #0ff;
  display: block;
}

.countdown-timer p {
  margin-top: 5px;
  font-size: 0.9rem;
  color: #f0f;
}

/* Footer */
.footer {
  text-align: center;
  margin-top: 50px;
  padding: 15px;
  background: #111;
  border-top: 2px solid #0ff;
  color: #0ff;
  font-size: 0.9rem;
  text-shadow: 0 0 6px #0ff;
}

.info-voting,
.aturan {
  text-align: left;
  /* rata kiri */
  padding: 15px;
  background: linear-gradient(145deg, #111, #1a1a1a);
  border: 2px solid #0ff;
  border-radius: 12px;
  box-shadow: 0 0 15px #0ff;
  color: #f0f0f0;
  margin-bottom: 20px;
}

.info-voting h2,
.aturan h2 {
  color:  #f0f;
  text-shadow: 0 0 5px  #f0f, 0 0 10px #0ff;
  margin-bottom: 10px;
}

.info-voting p,
.aturan ul {
  margin: 5px 0;
  line-height: 1.5;
}

.aturan ul {
  padding-left: 20px;
  /* beri jarak bullet list */
}

.statistik {
  text-align: left;
  /* rata kiri */
  padding: 15px;
  background: linear-gradient(145deg, #111, #1a1a1a);
  border: 2px solid #0ff;
  border-radius: 12px;
  box-shadow: 0 0 15px #0ff;
  color: #f0f0f0;
  margin-bottom: 20px;
}

.statistik h2 {
  color:  #f0f;
  text-shadow: 0 0 5px  #f0f, 0 0 10px #0ff;
  margin-bottom: 10px;
}

.statistik p {
  margin: 5px 0;
  line-height: 1.5;
}

/* Progress bar */
.progress-bar {
  background: #111;
  border: 1px solid #0ff;
  border-radius: 10px;
  overflow: hidden;
  height: 20px;
  margin-top: 10px;
}

.progress {
  height: 100%;
  background: #0ff;
  width: 0%;
  transition: width 0.5s ease-in-out;
}


/* === Responsif ke mobile === */
@media (max-width: 768px) {
  .card-container {
    grid-template-columns: repeat(1, minmax(280px, 1fr));
  }
}

frontend/src/styles/LoginPage.css.content:
/* Neon Minimalist Login Form - Complete & Self-Contained */

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
    background: #0a0a0f;
    min-height: 100vh;
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 20px;
    line-height: 1.6;
    overflow: hidden;
}

.login-container {
    width: 100%;
    max-width: 400px;
    position: relative;
    z-index: 10;
}

.login-card {
    background: #151520;
    border: 1px solid #2a2a35;
    border-radius: 16px;
    padding: 40px;
    box-shadow:
        0 20px 40px rgba(0, 0, 0, 0.3),
        0 0 0 1px rgba(255, 255, 255, 0.05);
    position: relative;
    backdrop-filter: blur(20px);
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.login-card::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 1px;
    background: linear-gradient(90deg, transparent, #00ff88, transparent);
    opacity: 0;
    transition: opacity 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.login-card:hover::before {
    opacity: 1;
}

.login-card:hover {
    box-shadow:
        0 20px 60px rgba(0, 0, 0, 0.4),
        0 0 0 1px rgba(0, 255, 136, 0.1),
        0 0 40px rgba(0, 255, 136, 0.1);
    transform: translateY(-2px);
}

.login-header {
    text-align: center;
    margin-bottom: 40px;
}

.logo-icon {
    font-size: 2.5rem;
    margin-bottom: 16px;
    background: linear-gradient(135deg, #00ff88, #0099ff);
    -webkit-background-clip: text;
    background-clip: text;
    -webkit-text-fill-color: transparent;
    filter: drop-shadow(0 0 20px rgba(0, 255, 136, 0.3));
    animation: pulse 2s ease-in-out infinite alternate;
}

.login-header h2 {
    color: #ffffff;
    font-size: 2rem;
    font-weight: 600;
    margin-bottom: 8px;
    letter-spacing: -0.02em;
}

.login-header p {
    color: #a0a0b0;
    font-size: 16px;
    font-weight: 400;
}

/* Form Group & Input Styles */
.form-group {
    margin-bottom: 24px;
    position: relative;
}

.input-wrapper {
    position: relative;
    margin-bottom: 24px;
}

.input-wrapper input {
    width: 100%;
    background: #1a1a25;
    border: 1px solid #2a2a35;
    border-radius: 6px;
    padding: 24px 16px;
    color: #ffffff;
    font-size: 16px;
    font-weight: 400;
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    outline: none;
}

.input-wrapper input:focus {
    border-color: #00ff88;
    background: rgba(26, 26, 37, 0.8);
    box-shadow:
        0 0 0 3px rgba(0, 255, 136, 0.1),
        0 4px 20px rgba(0, 255, 136, 0.1);
}

.input-wrapper input:focus+label,
.input-wrapper input:valid+label {
    transform: translateY(-32px) translateX(4px) scale(0.85);
    color: #00ff88;
    font-weight: 500;
}

.input-wrapper label {
    position: absolute;
    left: 16px;
    top: 24px;
    color: #a0a0b0;
    font-size: 16px;
    font-weight: 400;
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    pointer-events: none;
    transform-origin: left top;
}

.input-line {
    position: absolute;
    bottom: 0;
    left: 50%;
    width: 0;
    height: 2px;
    background: linear-gradient(90deg, #00ff88, #0099ff);
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    transform: translateX(-50%);
    border-radius: 2px;
}

.input-wrapper input:focus~.input-line {
    width: 100%;
}

/* Password Toggle */
.password-wrapper input {
    padding-right: 50px;
}

.password-toggle {
    position: absolute;
    right: 16px;
    top: 50%;
    transform: translateY(-50%);
    background: none;
    border: none;
    cursor: pointer;
    padding: 8px;
    color: #a0a0b0;
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    border-radius: 6px;
}

.password-toggle:hover {
    color: #00ff88;
    background: rgba(0, 255, 136, 0.1);
}

.toggle-icon {
    display: block;
    width: 20px;
    height: 20px;
    background-image: url("data:image/svg+xml,%3csvg xmlns='http://www.w3.org/2000/svg' fill='none' viewBox='0 0 24 24' stroke='%23a0a0b0' stroke-width='2'%3e%3cpath stroke-linecap='round' stroke-linejoin='round' d='M15 12a3 3 0 11-6 0 3 3 0 016 0z'/%3e%3cpath stroke-linecap='round' stroke-linejoin='round' d='M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z'/%3e%3c/svg%3e");
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.password-toggle:hover .toggle-icon {
    background-image: url("data:image/svg+xml,%3csvg xmlns='http://www.w3.org/2000/svg' fill='none' viewBox='0 0 24 24' stroke='%2300ff88' stroke-width='2'%3e%3cpath stroke-linecap='round' stroke-linejoin='round' d='M15 12a3 3 0 11-6 0 3 3 0 016 0z'/%3e%3cpath stroke-linecap='round' stroke-linejoin='round' d='M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z'/%3e%3c/svg%3e");
}

.toggle-icon.show-password {
    background-image: url("data:image/svg+xml,%3csvg xmlns='http://www.w3.org/2000/svg' fill='none' viewBox='0 0 24 24' stroke='%23a0a0b0' stroke-width='2'%3e%3cpath stroke-linecap='round' stroke-linejoin='round' d='M3.98 8.223A10.477 10.477 0 001.934 12C3.226 16.338 7.244 19.5 12 19.5c.993 0 1.953-.138 2.863-.395M6.228 6.228A10.45 10.45 0 0112 4.5c4.756 0 8.773 3.162 10.065 7.498a10.523 10.523 0 01-4.293 5.774M6.228 6.228L3 3m3.228 3.228l3.65 3.65m7.894 7.894L21 21m-3.228-3.228l-3.65-3.65m0 0a3 3 0 11-4.243-4.243m4.242 4.242L9.88 9.88'/%3e%3c/svg%3e");
}

/* Form Options */
.form-options {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 32px;
    flex-wrap: wrap;
    gap: 16px;
}

.remember-wrapper {
    display: flex;
    align-items: center;
    gap: 8px;
    cursor: pointer;
}

.remember-wrapper input[type="checkbox"] {
    display: none;
}

.custom-checkbox {
    width: 16px;
    height: 16px;
    border: 2px solid #2a2a35;
    border-radius: 3px;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    position: relative;
}

.custom-checkbox::after {
    content: '';
    width: 8px;
    height: 8px;
    background: #00ff88;
    border-radius: 1px;
    transform: scale(0);
    transition: transform 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.remember-wrapper input[type="checkbox"]:checked+.checkbox-label .custom-checkbox {
    border-color: #00ff88;
    background: rgba(0, 255, 136, 0.1);
    box-shadow: 0 0 10px rgba(0, 255, 136, 0.3);
}

.remember-wrapper input[type="checkbox"]:checked+.checkbox-label .custom-checkbox::after {
    transform: scale(1);
}

.checkbox-label {
    color: #a0a0b0;
    font-size: 14px;
    cursor: pointer;
    user-select: none;
    display: flex;
    align-items: center;
    gap: 8px;
    transition: color 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.remember-wrapper:hover .checkbox-label {
    color: #ffffff;
}

.forgot-password {
    color: #a0a0b0;
    text-decoration: none;
    font-size: 14px;
    font-weight: 500;
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    position: relative;
}

.forgot-password:hover {
    color: #00ff88;
}

/* Neon Button */
.login-btn {
    width: 100%;
    background: linear-gradient(135deg, #00ff88, #0099ff);
    border: none;
    border-radius: 6px;
    padding: 24px 32px;
    color: #0a0a0f;
    font-size: 16px;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    position: relative;
    overflow: hidden;
    margin-bottom: 24px;
    text-transform: uppercase;
    letter-spacing: 0.05em;
}

.btn-glow {
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.3), transparent);
    transition: left 0.5s cubic-bezier(0.4, 0, 0.2, 1);
}

.login-btn:hover {
    transform: translateY(-1px);
    box-shadow:
        0 10px 30px rgba(0, 255, 136, 0.3),
        0 0 40px rgba(0, 255, 136, 0.2);
}

.login-btn:hover .btn-glow {
    left: 100%;
}

.login-btn:active {
    transform: translateY(0);
}

/* Loading State */
.login-btn.loading {
    pointer-events: none;
}

.btn-text {
    transition: opacity 0.3s ease;
}

.btn-loader {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 20px;
    height: 20px;
    border: 2px solid transparent;
    border-top: 2px solid #0a0a0f;
    border-radius: 50%;
    opacity: 0;
    animation: spin 1s linear infinite;
    transition: opacity 0.3s ease;
}

.login-btn.loading .btn-text {
    opacity: 0;
}

.login-btn.loading .btn-loader {
    opacity: 1;
}

/* Divider */
.divider {
    text-align: center;
    margin: 32px 0 24px;
    position: relative;
}

.divider::before {
    content: '';
    position: absolute;
    top: 50%;
    left: 0;
    right: 0;
    height: 1px;
    background: linear-gradient(90deg, transparent, #2a2a35, transparent);
    transform: translateY(-50%);
}

.divider span {
    background: #151520;
    color: #a0a0b0;
    padding: 0 24px;
    font-size: 14px;
    text-transform: uppercase;
    letter-spacing: 0.1em;
    position: relative;
    z-index: 1;
}

/* Social Login */
.social-login {
    display: flex;
    flex-direction: column;
    gap: 16px;
    margin-bottom: 24px;
}

.social-btn {
    background: #1a1a25;
    border: 1px solid #2a2a35;
    border-radius: 6px;
    padding: 16px 24px;
    color: #ffffff;
    font-size: 14px;
    font-weight: 500;
    cursor: pointer;
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 16px;
}

.social-btn:hover {
    background: rgba(26, 26, 37, 0.8);
    border-color: #00ff88;
    transform: translateY(-1px);
    box-shadow: 0 4px 20px rgba(0, 255, 136, 0.1);
}

.social-icon {
    width: 20px;
    height: 20px;
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
}

.google-icon {
    background-image: url("data:image/svg+xml,%3csvg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 24 24'%3e%3cpath fill='%23ea4335' d='M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z'/%3e%3cpath fill='%2334a853' d='M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z'/%3e%3cpath fill='%23fbbc05' d='M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z'/%3e%3cpath fill='%23ea4335' d='M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z'/%3e%3c/svg%3e");
}

.apple-icon {
    background-image: url("data:image/svg+xml,%3csvg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 24 24' fill='white'%3e%3cpath d='M18.71 19.5c-.83 1.24-1.71 2.45-3.05 2.47-1.34.03-1.77-.79-3.29-.79-1.53 0-2 .77-3.27.82-1.31.05-2.3-1.32-3.14-2.53C4.25 17 2.94 12.45 4.7 9.39c.87-1.52 2.43-2.48 4.12-2.51 1.28-.02 2.5.87 3.29.87.78 0 2.26-1.07 3.81-.91.65.03 2.47.26 3.64 1.98-.09.06-2.17 1.28-2.15 3.81.03 3.02 2.65 4.03 2.68 4.04-.03.07-.42 1.44-1.38 2.83M13 3.5c.73-.83 1.94-1.46 2.94-1.5.13 1.17-.34 2.35-1.04 3.19-.69.85-1.83 1.51-2.95 1.42-.15-1.15.41-2.35 1.05-3.11z'/%3e%3c/svg%3e");
}

/* Signup Link */
.signup-link {
    text-align: center;
}

.signup-link p {
    color: #a0a0b0;
    font-size: 14px;
}

.signup-link a {
    color: #00ff88;
    text-decoration: none;
    font-weight: 500;
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.signup-link a:hover {
    color: #0099ff;
    text-shadow: 0 0 10px rgba(0, 153, 255, 0.5);
}

/* Error States */
.error-message {
    color: #ff0080;
    font-size: 12px;
    font-weight: 500;
    margin-top: 8px;
    margin-left: 4px;
    opacity: 0;
    transform: translateY(-10px);
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    background: rgba(255, 0, 128, 0.1);
    padding: 4px 8px;
    border-radius: 6px;
    border: 1px solid rgba(255, 0, 128, 0.2);
}

.error-message.show {
    opacity: 1;
    transform: translateY(0);
}

.form-group.error .input-wrapper input {
    border-color: #ff0080;
    background: rgba(255, 0, 128, 0.05);
    box-shadow: 0 0 0 3px rgba(255, 0, 128, 0.1);
    animation: shake 0.5s ease-in-out;
}

/* Success Message */
.success-message {
    display: none;
    text-align: center;
    padding: 40px 24px;
    opacity: 0;
    transform: translateY(20px);
    transition: all 0.5s cubic-bezier(0.4, 0, 0.2, 1);
}

.success-message.show {
    display: block;
    opacity: 1;
    transform: translateY(0);
}

.success-icon {
    width: 60px;
    height: 60px;
    background: linear-gradient(135deg, #00ff88, #0099ff);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 24px;
    color: #0a0a0f;
    margin: 0 auto 24px;
    animation: successPulse 0.6s cubic-bezier(0.68, -0.55, 0.265, 1.55);
    box-shadow: 0 0 30px rgba(0, 255, 136, 0.5);
}

.success-message h3 {
    color: #ffffff;
    font-size: 1.5rem;
    margin-bottom: 8px;
}

.success-message p {
    color: #a0a0b0;
    font-size: 16px;
}

/* Background Effects */
.background-effects {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    pointer-events: none;
    z-index: 1;
}

.glow-orb {
    position: absolute;
    border-radius: 50%;
    background: radial-gradient(circle, rgba(0, 255, 136, 0.1), transparent 70%);
    animation: float 6s ease-in-out infinite;
}

.glow-orb-1 {
    width: 300px;
    height: 300px;
    top: 10%;
    left: -10%;
    background: radial-gradient(circle, rgba(0, 255, 136, 0.08), transparent 70%);
    animation-delay: 0s;
}

.glow-orb-2 {
    width: 200px;
    height: 200px;
    top: 60%;
    right: -5%;
    background: radial-gradient(circle, rgba(0, 153, 255, 0.06), transparent 70%);
    animation-delay: -2s;
}

.glow-orb-3 {
    width: 150px;
    height: 150px;
    bottom: 20%;
    left: 10%;
    background: radial-gradient(circle, rgba(255, 0, 128, 0.04), transparent 70%);
    animation-delay: -4s;
}

/* Step Effect */
.step-indicator {
    display: flex;
    justify-content: space-between;
    margin: 20px 0;
    font-size: 14px;
    font-weight: 600;
    color: #888;
    position: relative;
}

.step-indicator::before {
    content: "";
    position: absolute;
    bottom: -5px;
    left: 0;
    width: 100%;
    height: 3px;
    background: rgba(255, 255, 255, 0.1);
    border-radius: 2px;
}

.step {
    flex: 1;
    text-align: center;
    position: relative;
    z-index: 1;
}

.step.active {
    color: #00ffe7;
}

.step.active::after {
    content: "";
    position: absolute;
    bottom: -5px;
    left: 0;
    right: 0;
    height: 3px;
    background: #00ffe7;
    box-shadow: 0 0 8px #00ffe7, 0 0 15px #00ffe7;
    border-radius: 2px;
}


/* Animations */
@keyframes pulse {
    from {
        filter: drop-shadow(0 0 20px rgba(0, 255, 136, 0.3));
    }

    to {
        filter: drop-shadow(0 0 30px rgba(0, 255, 136, 0.6));
    }
}

@keyframes float {
    0%, 100% {
        transform: translateY(0px) translateX(0px);
    }

    33% {
        transform: translateY(-20px) translateX(10px);
    }

    66% {
        transform: translateY(10px) translateX(-10px);
    }
}

@keyframes spin {
    0% {
        transform: translate(-50%, -50%) rotate(0deg);
    }

    100% {
        transform: translate(-50%, -50%) rotate(360deg);
    }
}

@keyframes shake {
    0%, 100% {
        transform: translateX(0);
    }

    25% {
        transform: translateX(-5px);
    }

    75% {
        transform: translateX(5px);
    }
}

@keyframes successPulse {
    0% {
        transform: scale(0);
    }

    50% {
        transform: scale(1.1);
    }

    100% {
        transform: scale(1);
    }
}


/* Mobile Responsive */
@media (max-width: 480px) {
    body {
        padding: 10px;
    }

    .login-card {
        padding: 24px;
        margin: 10px;
    }

    .login-header h2 {
        font-size: 1.75rem;
    }

    .input-wrapper input {
        padding: 16px;
    }

    .password-wrapper input {
        padding-right: 45px;
    }

    .form-options {
        flex-direction: column;
        align-items: flex-start;
        gap: 16px;
    }

    .glow-orb {
        opacity: 0.5;
    }

}

frontend/src/styles/Navbar.css.content:
.navbar {
  display: flex;
  justify-content: space-between;
  align-items: center;
  background: rgba(17, 17, 17, 0.95);
  /* semi transparan */
  backdrop-filter: blur(8px);
  /* blur belakang */
  padding: 15px 40px;
  width: 100%;
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  z-index: 1000;
  box-shadow: 0 4px 20px rgba(0, 255, 255, 0.3);
  /* glow bawah lebih dramatis */
  border-bottom: 2px solid #0ff;
  /* garis neon tipis */
}

.logo {
  font-size: 1.5rem;
  font-weight: bold;
  color: #0ff;
  text-shadow: 0 0 10px #0ff, 0 0 20px #0ff, 0 0 30px #0ff;
  letter-spacing: 1.5px;
}

.nav-links {
  list-style: none;
  display: flex;
  gap: 25px;
  margin: 0;
  padding: 0;
}

.nav-links li a {
  color: #f0f0f0;
  text-decoration: none;
  font-size: 1rem;
  transition: all 0.3s ease;
  position: relative;
}

.nav-links li a::after {
  content: "";
  position: absolute;
  width: 0%;
  height: 2px;
  bottom: -4px;
  left: 0;
  background: #0ff;
  transition: 0.3s;
}

.nav-links li a:hover::after {
  width: 100%;
}

.nav-links li a:hover {
  color: #0ff;
  text-shadow: 0 0 10px #0ff, 0 0 20px #0ff;
}

.special-btn {
  background: #f0f;
  color: #fff;
  padding: 8px 18px;
  border-radius: 20px;
  font-weight: 500;
  transition: all 0.3s ease;
  box-shadow: 0 0 12px #f0f, 0 0 20px #f0f;
  border: none;
  cursor: pointer;
}

.special-btn:hover {
  background: #c800c8;
  box-shadow: 0 0 20px #f0f, 0 0 30px #f0f;
  transform: scale(1.05);
}

frontend/src/styles/Statistik.css.content:
.statistik-page {
    padding: 20px;
    text-align: center;
    color: #f0f0f0;
}

.page-title {
    font-size: 2rem;
    margin-top: 20px;
    margin-bottom: 20px;
    color: #f0f;
    text-shadow: 0 0 10px #f0f, 0 0 20px #f0f;
}

.statistik-info {
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    gap: 20px;
    margin: 20px 0 40px;
}

.stat-box {
    background: linear-gradient(145deg, #111, #1a1a1a);
    border: 2px solid #0ff;
    border-radius: 12px;
    padding: 15px;
    box-shadow: 0 0 15px #0ff;
}

.stat-box h3 {
    color: #0ff;
    margin-bottom: 5px;
}

.chart-container {
    margin: 30px auto;
    padding: 20px;
    max-width: 800px;
    background: linear-gradient(145deg, #111, #1a1a1a);
    border: 2px solid #0ff;
    border-radius: 12px;
    box-shadow: 0 0 15px #0ff;
}

/* Tablet */
@media (max-width: 1024px) {
    .statistik-info {
        grid-template-columns: repeat(2, 1fr); /* 2 kolom */
        gap: 15px;
    }
}

/* Mobile */
@media (max-width: 600px) {
    .statistik-info {
        grid-template-columns: 2fr; /* 1 kolom */
        gap: 10px;
    }

    .stat-box {
        padding: 10px;
    }

    .page-title {
        font-size: 1.5rem;
    }

    .chart-container {
        padding: 15px;
        max-width: 100%;
    }
}

frontend/src/styles/VotePage.css.content:
#root {
    height: 100%;
    display: flex;
    flex-direction: column;
}

body {
    margin-top: 20px;
    /* kasih jarak setinggi navbar */
    overflow: hidden;
}

/* Container untuk halaman */
.container {
    margin-top: 120px;
    /* biar ga ketimpa navbar */
    padding: 20px;
    text-align: center;
}

/* Judul halaman */
.page-title {
    font-size: 2rem;
    margin-bottom: 30px;
    color: #00fff7;
    text-shadow: 0 0 10px #00fff7;
}

/* Grid kartu */
.card-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 25px;
    justify-content: center;
}

/* Kartu kandidat ala Pokemon */
.vote-card {
    background: linear-gradient(145deg, #1a1a1a, #0d0d0d);
    border: 3px solid #00fff7;
    border-radius: 16px;
    box-shadow: 0 0 15px rgba(0, 255, 247, 0.5),
        inset 0 0 20px rgba(0, 255, 247, 0.2);
    padding: 20px;
    transition: transform 0.2s ease, box-shadow 0.3s ease;
    text-align: center;
}

.vote-card:hover {
    transform: translateY(-8px) scale(1.02);
    box-shadow: 0 0 25px rgba(0, 255, 247, 0.8),
        inset 0 0 25px rgba(0, 255, 247, 0.3);
}

.vote-card h2 {
    font-size: 1.5rem;
    color: #ffcc00;
    margin-bottom: 10px;
    text-shadow: 0 0 8px #ffcc00;
}

.vote-card p {
    font-size: 0.95rem;
    color: #ccc;
    margin-bottom: 15px;
}

/* Tombol vote */
.vote-card button {
    background: #00fff7;
    color: #111;
    font-weight: bold;
    padding: 10px 20px;
    border: none;
    border-radius: 10px;
    cursor: pointer;
    transition: 0.3s;
}

.vote-card button:hover {
    background: #ffcc00;
    color: #111;
    box-shadow: 0 0 15px #ffcc00;
}

/* Efek kalau sudah dipilih */
.vote-card.selected {
    border-color: #ffcc00;
    box-shadow: 0 0 25px #ffcc00, inset 0 0 20px rgba(255, 204, 0, 0.3);
}

/* Hasil vote */
.result {
    margin-top: 25px;
    font-size: 1.2rem;
    color: #00ff9d;
    font-weight: bold;
}

smart-contract/ignition/modules/Lock.js.content:
// This setup uses Hardhat Ignition to manage smart contract deployments.
// Learn more about it at https://hardhat.org/ignition

const { buildModule } = require("@nomicfoundation/hardhat-ignition/modules");

const JAN_1ST_2030 = 1893456000;
const ONE_GWEI = 1_000_000_000n;

module.exports = buildModule("LockModule", (m) => {
  const unlockTime = m.getParameter("unlockTime", JAN_1ST_2030);
  const lockedAmount = m.getParameter("lockedAmount", ONE_GWEI);

  const lock = m.contract("Lock", [unlockTime], {
    value: lockedAmount,
  });

  return { lock };
});
